import dynamic from 'next/dynamic.js';
import { jsx } from 'react/jsx-runtime';

// src/comments/index.tsx
var UtterancesComponent = dynamic(
  () => {
    return import('./comments/Utterances.js').then((mod) => mod.Utterances);
  },
  { ssr: false }
);
var GiscusComponent = dynamic(
  () => {
    return import('./comments/Giscus.js').then((mod) => mod.Giscus);
  },
  { ssr: false }
);
var DisqusComponent = dynamic(
  () => {
    return import('./comments/Disqus.js').then((mod) => mod.Disqus);
  },
  { ssr: false }
);
var Comments = ({ commentsConfig, slug }) => {
  switch (commentsConfig.provider) {
    case "giscus":
      return /* @__PURE__ */ jsx(GiscusComponent, {
        ...commentsConfig.giscusConfig
      });
    case "utterances":
      return /* @__PURE__ */ jsx(UtterancesComponent, {
        ...commentsConfig.utterancesConfig
      });
    case "disqus":
      return /* @__PURE__ */ jsx(DisqusComponent, {
        slug,
        ...commentsConfig.disqusConfig
      });
  }
};

export { Comments };
