import React from 'react';
import { AlgoliaConfig } from './Algolia.js';
import { KBarConfig } from './KBar.js';
import '@docsearch/react';
import 'kbar';

declare type SearchConfig = AlgoliaConfig | KBarConfig;
interface SearchConfigProps {
    searchConfig: SearchConfig;
    children: React.ReactNode;
}
interface SearchQuery {
    setSearch: (search: string) => void;
    toggle: () => void;
}
/**
 * Command palette like search component - `ctrl-k` to open the palette.
 * Or use the search context to bind toggle to an onOpen event.
 * Currently supports Algolia or Kbar (local search).
 *
 * @param {SearchConfig} searchConfig
 * @return {*}
 */
declare const SearchProvider: ({ searchConfig, children }: SearchConfigProps) => JSX.Element;
interface SearchContext {
    query: SearchQuery;
}
declare const SearchContext: (provider: string) => React.Context<SearchContext>;

export { SearchConfig, SearchConfigProps, SearchContext, SearchProvider, SearchQuery };
